	UltMovFecha date NULL,
	UltMovLatitud NUMERIC NULL,
	UltMovLongitud NUMERIC NULL,
	UltMovPuntoCercano varchar(150) NULL
  ) ON COMMIT DROP;

  CREATE TEMPORARY TABLE  TRAKING(  
    DATESATELITE TIMESTAMP,
    LONGITUDE NUMERIC, 
    LATITUDE NUMERIC, 
    SPEED NUMERIC, 
    PUNTO_CERCANO VARCHAR( 200 ) 
  )  ON COMMIT DROP;


  INSERT INTO TRAKING
