              --evita formato en blanco
              v_formato = NULL;
              IF(v_parametros.formato is not NULL and trim(v_parametros.formato) <>'' )THEN
                v_formato =  v_parametros.formato;
              END IF;
               
               INSERT INTO param.tdocumento(
