			v_cadena = f_agrega_clave(v_cadena,v_clave,f_obtiene_clave_valor(p_claves,v_clave,'','','valor'));
			--Corta la cadena de claves
			v_aux=v_cad_ini_fin||v_clave||v_cad_fin_fin;
