			--raise exception '%',v_pos_aux;
			
			v_claves = substr(v_claves,v_pos_aux+length(v_aux),length(v_claves));
			--raise exception '%',v_claves;
		else
			exit;
		end if;

	END LOOP;
    --Devolver respuesta    
    return v_cadena;
  
END;
$$;


