/**************************************************************************
 SISTEMA ENDESIS - SISTEMA DE SEGURIDAD (SSS)
***************************************************************************
 SCRIPT: 		trisg_usuario
 DESCRIPCIN: 	Permite insertar, modificar y eliminar usuarios de la base
                de datos
 AUTOR: 		KPLIAN(rac)
 FECHA:			11-09-2010
 COMENTARIOS:	
***************************************************************************
 HISTORIA DE MODIFICACIONES:
 
 DESCRIPCION:	cambio que permite crear nombre de usuario con .
 AUTOR:			KPLIAN (rac)
 FECHA:			26/11/2011

***************************************************************************/
--------------------------
-- CUERPO DE LA FUNCIN --
--------------------------

--**** DECLARACION DE VARIABLES DE LA FUNCIN (LOCALES) ****---


DECLARE

    --PARMETROS FIJOS

    g_consulta         text;    -- VARIABLE QUE CONTENDR LA CONSULTA DINMICA PARA EL FILTRO
    g_new_login            text;
    g_old_login            text;
    y varchar;
    
    _SEMILLA varchar;
BEGIN

      --*** EJECUCIN DEL PROCEDIMIENTO ESPECFICO

  --procedimiento de creaci贸n de base de datos

_SEMILLA = '+_)(*&^%$#@!@TERPODO';


    IF TG_OP = 'INSERT' THEN

                BEGIN
                select (current_database()::text)||'_'||NEW.cuenta into g_new_login;
                     -- Creaci贸n Usuario
                     g_consulta := 'CREATE USER'||' "'||g_new_login||'"';
                     g_consulta := g_consulta||' '||'WITH ENCRYPTED PASSWORD '''||md5(_SEMILLA||NEW.contrasena)||'''';
                     g_consulta := g_consulta||' '||'VALID UNTIL '''||NEW.fecha_caducidad||'''';

                     EXECUTE(g_consulta);

                  
                     --ASGINACION DE ROLES
                     
                     y:='ALTER GROUP rol_usuario_'||current_database()::text||'
                     ADD USER  "'||g_new_login||'"';
                     EXECUTE (y); 
                     
         
                END;

          --procedimiento de modificacion de usuario

     
   ELSIF TG_OP = 'UPDATE' THEN

        BEGIN
        
            --raise exception  'llega';
            --Modificaci贸n de login
               select (current_database()::text)||'_'||NEW.cuenta into g_new_login;
               select (current_database()::text)||'_'||OLD.cuenta into g_old_login;
                 
                
           
                
             IF (OLD.cuenta != NEW.cuenta) THEN
               		 g_consulta := 'ALTER USER "'||g_old_login||'" RENAME TO "'||g_new_login||'"';
          
           		     EXECUTE(g_consulta);
                   
             END IF; 

             -- Modificaci贸n de la contrase帽a

            IF (OLD.contrasena != NEW.contrasena) THEN

                    g_consulta := NULL;
                    g_consulta := 'ALTER USER "'||g_new_login||'" ';
                    g_consulta := g_consulta||' '||'WITH ENCRYPTED PASSWORD '''||md5(_SEMILLA||NEW.contrasena)||'''';

                    EXECUTE(g_consulta);
                    
                   
                    
            END IF;

                 -- Modificaci贸n de la Fecha de Validez del usuario

            IF (OLD.fecha_caducidad != NEW.fecha_caducidad) THEN

                    g_consulta := NULL;
                    g_consulta := 'ALTER USER "'||g_new_login||'" ';
                    g_consulta := g_consulta||' '||'VALID UNTIL '''||NEW.fecha_caducidad||'''';

                    EXECUTE(g_consulta);

            END IF;
            
            
                   -- Modificaci贸n de la Fecha de Validez del usuario

           IF (NEW.estado_reg != OLD.estado_reg) THEN

                  IF (NEW.estado_reg = 'inactivo') THEN

                          g_consulta := NULL;
                          g_consulta := 'ALTER USER "'||g_new_login||'" ';
                          g_consulta := g_consulta||' '||'VALID UNTIL '''||now()||'''';
                          
                          EXECUTE(g_consulta);
                   ELSE      
                   
                          g_consulta := NULL;
                          g_consulta := 'ALTER USER "'||g_new_login||'"';
                          g_consulta := g_consulta||' '||'VALID UNTIL '''||NEW.fecha_caducidad||'''';

                          EXECUTE(g_consulta);
                   END IF;
            END IF;


        END;

  --procedimiento de eliminaci贸n de usuario

   ELSIF TG_OP = 'DELETE' THEN

        BEGIN
        select (current_database()::text)||'_'||OLD.cuenta into g_old_login;
             g_consulta := 'DROP USER "'||g_old_login||'"';
            
             EXECUTE(g_consulta);

        END;
        --*/

   END IF;

   RETURN NULL;

END;
$_$;


