 	#AUTOR:		rac	
 	#FECHA:		16-03-2012 17:06:17
	***********************************/

	IF(p_transaccion='RH_INIUOARB_SEL')then
raise notice '11111111';
                    
   CREATE TEMPORARY TABLE organigrama_filtro (
                                  "niveles" varchar,
                                  "id_uo" INTEGER, 
                                  "nombre_unidad" VARCHAR, 
                                  "nombre_cargo" VARCHAR, 
                                  "cargo_individual" VARCHAR, 
                                  "descripcion" VARCHAR, 
