                     (  niveles,
                        id_uo, 
                        nombre_unidad, 
                        nombre_cargo, 
                        cargo_individual, 
                        descripcion, 
                        presupuesta, 
                        codigo, 
                        nodo_base, 
                        gerencia, 
                        id_estructura_uo, 
                        correspondencia,
                        estado_reg,
                        funcionarios,
