                     (  v_nivel::varchar,
                        g_registros.id_uo, 
                        g_registros.nombre_unidad, 
                        g_registros.nombre_cargo, 
                        g_registros.cargo_individual, 
                        g_registros.descripcion, 
                        g_registros.presupuesta, 
                        g_registros.codigo, 
                        g_registros.nodo_base, 
