                        'si', --RESALTA LOS REGISTRO QUE COINCIDEN CON EL FILTRO DE LA BUSQUEDA
                        g_registros.id_uo_padre
                        );        
              END IF;        
       END LOOP;
       
       raise notice '33333333';

  --   2) lista la tabla temporal y devuelve el resultado
  
  FOR g_registros in (SELECT                                   
                        niveles,
