             select s.id_subsistema into v_id_subsistema
             from  segu.tsubsistema s
             where s.codigo = v_codigo;                                                
  
  
  IF(v_id_subsistema is null) THEN
  
  		raise exception 'solo nodo de sistema pueden migrarse o el sistema con dodigo % no existe en el destino',v_codigo;
  
  END IF;
  
  
  --      2)  crear tabla temporal de equivalencias
  create 
  temporary 
  table t_gui_equivalencia_temp (id_gui_orig integer , 
                                 id_gui_dest integer) on commit drop;


  --      3)  for para recorrer los resultados
  
  raise notice 'inicia FOR';
  
  FOR objeto IN   (SELECT id_gui , 
                                        nombre , 
                                        descripcion , 
                                        fecha_reg , 
                                        codigo_gui , 
                                        visible , 
                                        orden_logico , 
                                        ruta_archivo , 
                                        nivel , 
                                        icono , 
                                        id_subsistema, 
                                        clase_vista,
                                        fk_id_gui
                               FROM dblink(p_cadena_nonexion,
                                          'WITH RECURSIVE recursegui( id_gui , 
                                        nombre , 
                                        descripcion , 
                                        fecha_reg , 
                                        codigo_gui , 
                                        visible , 
                                        orden_logico , 
                                        ruta_archivo , 
                                        nivel , 
                                        icono , 
                                        id_subsistema, 
                                        clase_vista,
                                        fk_id_gui ) AS (
                                          SELECT g.id_gui , 
                                                 g.nombre , 
                                                 g.descripcion , 
                                                 g.fecha_reg , 
                                                 g.codigo_gui , 
                                                 g.visible , 
                                                 g.orden_logico , 
                                                 g.ruta_archivo , 
                                                 g.nivel , 
                                                 g.icono , 
                                                 g.id_subsistema, 
                                                 g.clase_vista,
                                                 eg.fk_id_gui
                                          FROM segu.tgui g 
                                          join segu.testructura_gui eg 
                                               on g.id_gui = eg.id_gui
                                          WHERE  g.id_gui= '||p_id_gui::varchar||'
                                        UNION
                                          SELECT  g.id_gui , 
                                                 g.nombre , 
                                                 g.descripcion , 
                                                 g.fecha_reg , 
                                                 g.codigo_gui , 
                                                 g.visible , 
                                                 g.orden_logico , 
                                                 g.ruta_archivo , 
                                                 g.nivel , 
                                                 g.icono , 
                                                 g.id_subsistema, 
                                                 g.clase_vista,
                                                 eg.fk_id_gui
                                          FROM segu.tgui g 
                                          join segu.testructura_gui eg 
                                               on g.id_gui = eg.id_gui
                                          JOIN recursegui  rt ON rt.id_gui = eg.fk_id_gui
                                        )
                                      SELECT * FROM recursegui;') AS P(
                                                                         id_gui INTEGER, 
                                                                          nombre VARCHAR(50), 
                                                                          descripcion TEXT, 
                                                                          fecha_reg DATE , 
                                                                          codigo_gui VARCHAR, 
                                                                          visible VARCHAR, 
                                                                          orden_logico INTEGER, 
                                                                          ruta_archivo TEXT, 
                                                                          nivel INTEGER, 
                                                                          icono VARCHAR, 
                                                                          id_subsistema INTEGER, 
