                              AND  g.id_subsistema = '||v_parametros.id_subsistema;
                 v_join:= 'LEFT';      
                      
              else
                v_where := ' eg.fk_id_gui = '||v_parametros.id_padre;
                v_join := 'INNER';
              end if;
          

                               v_consulta =  'SELECT
