	v_parametros  		record;
	v_nombre_funcion   	text;
	v_resp				varchar;
			
BEGIN

	v_nombre_funcion = 'segu.ft_programa_sel';
    v_parametros = f_get_record(p_tabla);

	/*********************************
 	#TRANSACCION:  'SG_PROGRA_SEL'
