 	#AUTOR:		w	
 	#FECHA:		13-08-2011 16:32:52
	***********************************/

	if(p_transaccion='SG_PROGRA_SEL')then
     				
    	begin
    		--Sentencia de la consulta
			v_consulta:='select
						progra.id_programa,
						progra.codigo,
						progra.descripcion,
						progra.estado_reg,
						progra.nombre,
						progra.fecha_reg,
						progra.id_usuario_reg,
						progra.fecha_mod,
						progra.id_usuario_mod,
						usu1.cuenta as usr_reg,
